module.exports = function(grunt) {
  'use strict';
  var ejs = require('ejs');
  grunt.registerMultiTask('ejs-compile', 'compile ejs-compile templates', function() {
    var options = this.options();
    this.files.forEach(function(file) {
      // prevents options declared / overrided
      // on file level to be moved to the next file
      options = this.options();
      var out = file.src.map(grunt.file.read).join('');
      options.filename = file.src[0];
      grunt.file.write(file.dest, ejs.compile(out, options));
      grunt.log.ok('compile success ' + file.dest);
    }, this);
  });
};
